import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import apiService from './services/apiService';
import socketService from './services/socketService';
import './App.css';

// Components
import PostForm from './components/PostForm';
import PostList from './components/PostList';
import CollaborationForm from './components/CollaborationForm';
import Dashboard from './components/Dashboard';
import PaymentModal from './components/PaymentModal';
import SuccessStories from './components/SuccessStories';
import QuotesSection from './components/QuotesSection';
import AdminDashboard from './components/AdminDashboard';
import TestDashboard from './components/TestDashboard';

function App() {
  const [posts, setPosts] = useState([]);
  const [stats, setStats] = useState({});
  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [selectedPost, setSelectedPost] = useState(null);

  useEffect(() => {
    fetchPosts();
    fetchStats();
  }, []);

  const fetchPosts = async () => {
    try {
      console.log('üîÑ Fetching posts from API...');
      const posts = await apiService.getPosts();
      console.log('üìã Received posts:', posts);
      
      // Log each post's donation data
      posts.forEach(post => {
        console.log(`  - ${post.title}: ‚Çπ${post.donationAmount} from ${post.donations} donors`);
      });
      
      setPosts(posts);
      console.log('‚úÖ Posts state updated');
    } catch (error) {
      console.error('‚ùå Error fetching posts:', error);
    }
  };

  const fetchStats = async () => {
    try {
      console.log('üìä Fetching stats from API...');
      const stats = await apiService.getStats();
      console.log('üìà Received stats:', stats);
      console.log(`  - Total Donations: ‚Çπ${stats.totalDonations}`);
      console.log(`  - Stories Shared: ${stats.totalPosts}`);
      console.log(`  - Collaborations: ${stats.totalCollaborations}`);
      
      setStats(stats);
      console.log('‚úÖ Stats state updated');
    } catch (error) {
      console.error('‚ùå Error fetching stats:', error);
    }
  };

  const handleDonate = (post) => {
    setSelectedPost(post);
    setShowPaymentModal(true);
  };

  const handleDonationComplete = () => {
    setShowPaymentModal(false);
    setSelectedPost(null);
    fetchPosts();
    fetchStats();
  };

  return (
    <Router>
      <div className="App">
        <nav className="navbar">
          <div className="nav-container">
            <Link to="/" className="nav-logo">
              ü§ù Humanity
            </Link>
            <div className="nav-menu">
              <Link to="/" className="nav-link">üè† Home</Link>
              <Link to="/post" className="nav-link">üìù Share Story</Link>
              <Link to="/success-stories" className="nav-link">üåü Success Stories</Link>
              <Link to="/collaborate" className="nav-link">ü§ù Collaborate</Link>
              <Link to="/dashboard" className="nav-link">üìä Dashboard</Link>
              <Link to="/admin" className="nav-link">üîê Admin</Link>
            </div>
          </div>
        </nav>

        <main className="main-content">
          <Routes>
            <Route path="/" element={
              <div>
                <section className="hero">
                  <div className="hero-content">
                    <h1>‚ú® Help Those in Need ‚ú®</h1>
                    <p>Share stories, make donations, and collaborate to make a difference in people's lives.</p>
